import React, { Component } from 'react';

import './home.scss'
import FinalForm from '../components/forms/Form'
import Smiley from '../components/smileys/Smiley'


class Home extends Component {
  state = {
    formVisible: true,
    msgVisible: false,
    stayArea: '50%',
    goArea: '50%',
    goDisplay: 'flex'
  }

  sendDataToCustomerCare = (clientData) => {
    console.log(clientData);
    this.sayGoodbye()
    setTimeout(() => window.location.replace("https://easylife-dc.be/"), 6000 )
  }

  sayGoodbye = () => {
    this.setState({
      stayArea: '0%',
      goArea: '100%',
    })  
  }

  stay = () => {
    this.setState({
      stayArea: '100%',
      goArea: '0%',
      goDisplay: 'none'
    }) 

  }

  toggleFormVisibility = () => {
    this.setState({
      formVisible: false,
      msgVisible: true
    })
  }

  render() {
    const { stayArea, goArea, goDisplay } = this.state
    return (
      <div className='home'>
        <div className='home-stay' style={{ width: stayArea }} onClickCapture={this.stay}>
          <h1 className='home-stay-title'>Stay</h1>
          <Smiley color={'#FBFFFE'}>&#128515;</Smiley>
        </div>
        <div className='home-go' style={{ width: goArea, display: goDisplay }}>
          <h1 className='home-go-title'>Go</h1>
          <Smiley color={'#F4AC45'}>&#128542;</Smiley>
        </div>
        <FinalForm sendData={this.sendDataToCustomerCare} formVisible={this.state.formVisible} msgVisible={this.state.msgVisible} toggleFormVisibility={this.toggleFormVisibility} />
      </div>
    )
  }
}

export default Home
