import React from 'react'
import { Message, Transition } from 'semantic-ui-react'

import './msg.scss'

const UiMessage = (props) => {

  const handleDismiss = () => {
    this.props.closeMsg()
  }

  return (
    <Transition visible={props.msgVisible} animation='scale' duration={1500}>
      <Message
        className='leave-msg'
        onDismiss={handleDismiss}
        visible={props.msgVisible}
        attached={'top'}
        success
        header='Thank you for your confirmation'
        content='Our Customer Care team will be in touch to conclude your request'
      />
    </Transition>  
  )
}


export default UiMessage
