import React, { Component } from 'react';

import './home.scss'
import FinalForm from '../components/forms/Form'
import Smiley from '../components/smileys/Smiley'


class Home extends Component {
  state = {
    stayArea: '50%',
    goArea: '50%',
  }

  sendDataToCustomerCare = (clientData) => {
    console.log(clientData);
    this.sayGoodbye()
    setTimeout(() => window.location.replace("https://easylife-dc.be/"),4000 )
  }

  sayGoodbye = () => {
    this.setState({
      stayArea: '0%',
      goArea: '100%'
    })  
  }

  stay = () => {
    console.log('stay');

  }

  render() {
    const { stayArea, goArea } = this.state
    return (
      <div className='home'>
        <div className='home-stay' style={{ width: stayArea }} onClickCapture={e => console.log(e)}>
          <h1 className='home-stay-title'>Stay</h1>
          <Smiley color={'#FBFFFE'}>&#128515;</Smiley>
        </div>
        <div className='home-go' style={{ width: goArea }}>
          <h1 className='home-go-title'>Go</h1>
          <Smiley color={'#F4AC45'}>&#128542;</Smiley>
        </div>
        <FinalForm sendData={this.sendDataToCustomerCare}/>
      </div>
    )
  }
}

export default Home
